SELECT d.id ID, d.fecha FECHA, d.tipo ID_TIPO_DOCUMENTO, d.tercero ID_TERCERO, t.nit NIT_TERCERO, t.nombre NOMBRE_TERCERO, d.numero NUMERO, d.total TOTAL, d.nota NOT2A, d.tipopago ID_TIPO_PAGO, d.fechavencimiento FECHA_VENCIMIENTO, d.totalpagado TOTAL_PAGADO, d.tipodescuento ID_TIPO_DESCUENTO, d.subtotal SUBTOTAL, d.descuento DESCUENTO, d.estado ESTADO FROM documento d, tercero t WHERE d.numero LIKE IFNULL(#{numero,jdbcType=VARCHAR},'%')
AND d.fecha = #{fecha,jdbcType=DATE}
AND t.identificacion LIKE IFNULL(#{tercero.identificacion,jdbcType=INTEGER},'%') OR t.nombre LIKE IFNULL(#{tercero.nombre,jdbcType=VARCHAR},'%')

SELECT
    d.id               ID,
    d.fecha            FECHA,
    d.tipo             ID_TIPO_DOCUMENTO,
    d.tercero          ID_TERCERO,
    t.nit              NIT_TERCERO,
    t.nombre           NOMBRE_TERCERO,
    d.numero           NUMERO,
    d.total            TOTAL,
    d.nota             NOTA,
    d.tipopago         ID_TIPO_PAGO,
    d.fechavencimiento FECHA_VENCIMIENTO,
    d.totalpagado      TOTAL_PAGADO,
    d.tipodescuento    ID_TIPO_DESCUENTO,
    d.subtotal         SUBTOTAL,
    d.descuento        DESCUENTO,
    d.estado           ESTADO
FROM
    documento d, tercero t
WHERE
   ( d.numero     LIKE  #{busqueda,jdbcType=VARCHAR}
    OR  d.fecha  LIKE  #{busqueda,jdbcType=VARCHAR}
    OR  t.nit    LIKE  #{busqueda,jdbcType=VARCHAR}
    OR  t.nombre LIKE  #{busqueda,jdbcType=VARCHAR}
    OR  d.total  LIKE  #{busqueda,jdbcType=VARCHAR}
    )
    AND
   (    d.tercero = t.id
    AND
         (  d.tipo = #{tipo,jdbcType=INTEGER}

    OR
        d.tipo = #{tipo2,jdbcType=DATE}


    AND
        d.estado = #{estado,jdbcType=VARCHAR}

        )
  ) ORDER BY d.fecha desc, d.numero desc
